import {Component, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {ActivatedRoute, Router} from "@angular/router";

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css']
})
export class OrdersComponent implements OnInit{
  orders:any;
  customerId!:number;

  constructor(private http:HttpClient, private router:Router, private route:ActivatedRoute) {
    this.customerId=route.snapshot.params['customerId'];
  }
  ngOnInit(): void {
    this.http.get("http://localhost:9999/order-service/orders/search/byCustomerId?projection=fullOrder&customerId="+this.customerId)
        .subscribe({
          next : (data)=>{
            this.orders=data;
          },
          error : (err)=>{}
        });
  }

}
<div class="container">
  <table class="table" *ngIf="orders">
    <thead>
    <tr>
      <th>ID</th>
      <th>Date</th>
      <th>Status</th>
      <th>CustomerId</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor="let o of orders._embedded.orders">
      <td>{{o.id}}</td>
      <td>{{o.createdAt|date:'dd/MM/yyyy'}}</td>
      <td>{{o.status}}</td>
      <td>{{o.customerId}}</td>
      <td>
        <button class="btn btn-success" (click)="getOrders(c)">Order Deatails</button>
      </td>
    </tr>
    </tbody>
  </table>
</div>
